// File: contracts/CustomERC20PresetMinterPauser.sol
import "./ERC20PresetMinterPauser.sol";
// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

// its admin should be factory,
// after deployment set owner as factory and revoke own role
contract CloneableMintableERC20 is ERC20PresetMinterPauser {
    constructor(string memory name, string memory symbol, uint8 decs) public ERC20PresetMinterPauser(name, symbol) {}

    function init(string memory name, string memory symbol, address factoryInteractor, uint8 decimals) public {
        require(!hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "ERC20PresetMinterPauser: must have admin role to init");
        _setupRole(DEFAULT_ADMIN_ROLE, factoryInteractor);
        _setupDecimals(decimals);
         _name = name;
        _symbol = symbol;

        renounceRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }
}
