pragma solidity 0.6.12;

import "./Ownable.sol";
import "./CloneFactory.sol";
import "./FDERC20Mintable.sol";

contract MintableCoinFactory is Ownable, CloneFactory {
    address public mintableERC20Address;
    event MintableERC20Created(address newERC20Address);

    constructor() public {}

    function createERC20Mintable(string memory name, string memory symbol, uint8 decimals) public onlyOwner returns (address) {
        address clone = createClone(mintableERC20Address);
        FDERC20Mintable(clone).init(name, symbol, owner(), decimals);
        MintableERC20Created(clone);
        return clone;
    }

    function setMintableContractAddress(address contractAddr) public onlyOwner {
        mintableERC20Address = contractAddr;
    }

    function getMintableContractAddress() external view returns (address) {
        return mintableERC20Address;
    }
}
