pragma solidity 0.6.12;

import "./Ownable.sol";
import "./CloneFactory.sol";
import "./CloneableMintableERC20.sol";
import "./Ownable.sol";
import "./Context.sol";

contract MintableCoinFactory is CloneFactory, Context, Ownable {
    address public mintableERC20Address;
    event MintableERC20Created(address newERC20Address);

    constructor() public {}

    function createERC20Mintable(string memory name, string memory symbol, uint8 decimals) public returns (address) {
        address clone = createClone(mintableERC20Address);
        CloneableMintableERC20(clone).init(name, symbol, _msgSender(), decimals);
        MintableERC20Created(clone);
        return clone;
    }

    function setMintableContractAddress(address contractAddr) public onlyOwner {
        mintableERC20Address = contractAddr;
    }

}
